+    public static String realTargetRef(PullRequest pr) {
+        Optional<String> idOpt = dependentPullRequestId(pr);
+        return idOpt.isEmpty() ? pr.targetRef() : realTargetRef(pr.repository().pullRequest(idOpt.get()));
+    }
