@@ -131,1 +131,10 @@
-        addFailureMessage(e.check(), "Merge commit message is not " + e.expected() + ", but: " + message);
+        var desc = "Merge commit message is not `" + e.expected() + "`, but:";
+        if (e.commit().message().size() == 1) {
+            desc += " `" + message + "`";
+        } else {
+            desc += "\n" +
+                    "```\n" +
+                    message +
+                    "```";
+        }
+        addFailureMessage(e.check(), desc);
