+    private void fetchWithRetry(Repository repo, URI url) throws IOException {
+        IOException lastException = null;
+        for (int count = 0; count < 10; ++count) {
+            try {
+                repo.fetchAll(url, true);
+                return;
+            } catch (IOException e) {
+                lastException = e;
+            }
+        }
+
+        throw lastException;
+    }
