+    // Sometimes GitLab returns merge requests that cannot be acted upon
+    private boolean mergeRequestValid(JSONValue mrJson) {
+        var obj = mrJson.asObject();
+        if (!obj.contains("sha") || obj.get("sha").isNull()) {
+            return false;
+        }
+
+        return true;
+    }
