+          vk->do_nonstatic_fields(&init_fields);
+        } else {
+          oop value = vk->allocate_instance(Thread::current());
+          _vt->obj_field_put(fd->offset(), value);
+        }
+        break;
+      }
+      default: {
+        fatal("Unhandled type: %s", type2name(bt));
+      }
+      }
