+    MutexLocker mu(THREAD, SystemDictionary_lock);
+    placeholders()->find_and_remove(p_index, p_hash, class_name, loader_data,
+                                    PlaceholderTable::INLINE_TYPE_FIELD, THREAD);
+  }
+
+  class_name->decrement_refcount();
+  return klass;
+}
