+    case T_OBJECT:
+    case T_ARRAY:
+      if (group != _static_fields) {
+        _nonstatic_oopmap_count++;
+        field_alignment = type2aelembytes(type); // alignment == size for oops
+      }
+      group->add_oop_field(fs);
+      break;
+    case T_INLINE_TYPE: {
+//      fs.set_inline(true);
+      _has_inline_type_fields = true;
+      if (group == _static_fields) {
+        // static fields are never inlined
+        group->add_oop_field(fs);
+      } else {
