+  assert(idx < java_fields_count(), "IOOB");
+  Klass* k = ((Klass**)adr_inline_type_field_klasses())[idx];
+  assert(k != NULL, "Should always be set before being read");
+  assert(k->is_inline_klass(), "Must be an inline type");
+  return k;
