+  assert(has_inline_type_fields(), "Sanity checking");
+  assert(idx < java_fields_count(), "IOOB");
+  Klass* k = ((Klass**)adr_inline_type_field_klasses())[idx];
+  assert(k == NULL || k->is_inline_klass(), "Must be an inline type");
+  return k;
