-    public static String[] concat(String prefix[], String... extra) {
-        ArrayList<String> list = new ArrayList<String>();
-        if (prefix != null) {
-            for (String s : prefix) {
-                list.add(s);
-            }
-        }
-        if (extra != null) {
-            for (String s : extra) {
-                list.add(s);
-            }
-        }
-
-        return list.toArray(new String[list.size()]);
-    }
