+//------------------------------eq---------------------------------------------
+// Structural equality check for Type representations
+bool TypeInlineType::eq(const Type* t) const {
+  const TypeInlineType* vt = t->is_inlinetype();
+  return (_vk == vt->inline_klass() && _larval == vt->larval());
+}
