+Klass* InlineKlass::value_array_klass_or_null(int n) {
+  // Need load-acquire for lock-free read
+  ArrayKlass* ak = Atomic::load_acquire(adr_value_array_klasses());
+  if (ak == NULL) {
+    return NULL;
+  } else {
+    return ak->array_klass_or_null(n);
+  }
+}
