+    // TODO 8284443 Avoid creation of temporary frame
+    if (ra_->C->stub_function() == NULL) {
+      __ verified_entry(ra_->C, 0);
+      __ entry_barrier();
+      int framesize = ra_->C->output()->frame_slots() << LogBytesPerInt;
+      __ remove_frame(framesize, false);
