+      src_bt = (src_bt == T_FLOAT) ? T_INT : T_LONG;
+    }
+    uint length_in_bytes_src = Matcher::vector_length_in_bytes(this, $src);
+    __ neon_vector_narrow($dst$$FloatRegister, dst_bt,
+                          $src$$FloatRegister, src_bt, length_in_bytes_src);
+  %}
+  ins_pipe(pipe_slow);
+%}
