+package jdk.vm.ci.riscv64;
+
+import jdk.vm.ci.meta.PlatformKind;
+
+public enum RISCV64Kind implements PlatformKind {
+
+    // scalar
+    BYTE(1),
+    WORD(2),
+    DWORD(4),
+    QWORD(8),
+    SINGLE(4),
+    DOUBLE(8);
+
+    private final int size;
+    private final int vectorLength;
+
+    private final RISCV64Kind scalar;
+    private final EnumKey<RISCV64Kind> key = new EnumKey<>(this);
+
+    RISCV64Kind(int size) {
+        this.size = size;
+        this.scalar = this;
+        this.vectorLength = 1;
+    }
+
+    RISCV64Kind(int size, RISCV64Kind scalar) {
+        this.size = size;
+        this.scalar = scalar;
+
+        assert size % scalar.size == 0;
+        this.vectorLength = size / scalar.size;
+    }
+
+    public RISCV64Kind getScalar() {
+        return scalar;
+    }
+
+    @Override
+    public int getSizeInBytes() {
+        return size;
+    }
+
+    @Override
+    public int getVectorLength() {
+        return vectorLength;
+    }
+
+    @Override
+    public Key getKey() {
+        return key;
+    }
+
+    public boolean isInteger() {
+        switch (this) {
+            case BYTE:
+            case WORD:
+            case DWORD:
+            case QWORD:
+                return true;
+            default:
+                return false;
+        }
+    }
+
+    public boolean isFP() {
+        switch (this) {
+            case SINGLE:
+            case DOUBLE:
+                return true;
+            default:
+                return false;
+        }
+    }
+
+    @Override
+    public char getTypeChar() {
+        switch (this) {
+            case BYTE:
+                return 'b';
+            case WORD:
+                return 'w';
+            case DWORD:
+                return 'd';
+            case QWORD:
+                return 'q';
+            case SINGLE:
+                return 'S';
+            case DOUBLE:
+                return 'D';
+            default:
+                return '-';
+        }
+    }
+
+}

