-        int bytesRead = 0;
-        while (bytesRead < b.length) {
-            int toRead = Math.min(b.length - bytesRead, buffer.capacity());
-            ensureAvailable(toRead);
-            buffer.get(b, bytesRead, toRead);
-            bytesRead += toRead;
-        }
-        return b;
-    }
