-                break;
-            }
-            case CLOSE_GROUP: {
-                if (folderStack.isEmpty()) {
-                    throw new IOException("Unbalanced groups");
-                }
-                folderStack.pop();
-                hashStack.pop();
-                break;
-            }
-            default:
-                throw new IOException("unknown root : " + type);
