-    }
-
-    public void remove() {
-        source.getGraph().removeEdge(this, null);
-    }
-
-    public boolean isSelfLoop() {
-        return source == dest;
-    }
-
-    public void reverse() {
-
-        // Remove from current source / dest
-        source.removeOutEdge(this);
-        dest.removeInEdge(this);
-
-        Node<N, E> tmp = source;
-        source = dest;
-        dest = tmp;
-
-        // Add to new source / dest
-        source.addOutEdge(this);
-        dest.addInEdge(this);
-    }
-
