-    private void traverse(DFSTraversalVisitor tv, Node<N, E> n) {
-
-        if (!n.isVisited()) {
-            n.setVisited(true);
-            n.setActive(true);
-            tv.visitNode(n);
-
-            for (Edge<N, E> e : n.getOutEdges()) {
-
-                Node<N, E> next = e.getDest();
-                if (next.isActive()) {
-                    tv.visitEdge(e, true);
-                } else {
-                    if (tv.visitEdge(e, false)) {
-                        traverse(tv, next);
-                    }
-                }
-            }
-
-            n.setActive(false);
-        }
-
-    }
