-        }
-
-        if (!n.isVisited()) {
-
-            n.setVisited(true);
-            n.setActive(true);
-
-            for (Node<N, E> succ : n.getSuccessors()) {
-                if (checkCycles(succ)) {
-                    return true;
-                }
-            }
-
-            n.setActive(false);
-
-        }
-
-        return false;
-    }
