+                throw new RuntimeException("Output mismatch on line " + i
+                                           + ": a=" + a[i]
+                                           + ", b=" + b[i]);
+            }
+        }
+
+        // Report the first line that is in one array but not in the other
+        if (a.length > b.length) {
+            throw new RuntimeException("Output mismatch on line " + limit
+                                       + ": a=" + a[limit]
+                                       + ", b=<none>");
+
+        }
+        if (a.length < b.length) {
+            throw new RuntimeException("Output mismatch on line " + limit
+                                       + ": a=<none>"
+                                       + ", b=" + b[limit]);
+        }
+    }

