+
+    public void testRefProjection() {
+        assertFail("compiler.err.prob.found.req",
+                """
+                primitive class PC {
+                    void foo() {
+                        PC x = null;
+                    }
+                }
+                """);
+        assertOK(
+                """
+                primitive class PC {
+                    void foo() {
+                        PC.ref x = null;
+                    }
+                }
+                """);
+        assertOK(
+                """
+                primitive class PC {
+                    void foo(PC x) {
+                        PC.ref xq = null;
+                        xq = x;
+                        xq = (PC.ref) x;
+                        xq = (PC) x;
+                        x = xq;
+                        x = (PC.ref) xq;
