+        } else {
+          new_value_h()->obj_field_put(offset, ref_h());
+        }
+      }
+      break;
+    default:
+      ShouldNotReachHere();
+  }
+  current->set_vm_result(new_value_h());
+  return ret_adj;
+JRT_END
