+void InlineKlass::write_inlined_field(oop obj, int offset, oop value, TRAPS) {
+  if (value == NULL) {
+    THROW(vmSymbols::java_lang_NullPointerException());
+  }
+  if (!is_empty_inline_type()) {
+    inline_copy_oop_to_payload(value, ((char*)(oopDesc*)obj) + offset);
+  }
