+  *((address*)adr_pack_handler_jobject()) = NULL;
+  *((address*)adr_unpack_handler()) = NULL;
+  assert(pack_handler() == NULL, "pack handler not null");
+  if (value_array_klasses() != NULL) {
+    value_array_klasses()->remove_unshareable_info();
+  }
+}
