+    apush(append_split(new_instance));
+  } else {
+    new_instance = new NewInlineTypeInstance(holder->as_inline_klass(), state_before);
+    _memory->new_instance(new_instance);
+    apush(append_split(new_instance));
+
+    // Initialize fields which are not modified
